update templates set content='<img src=http://snapsnare.org/static/snapsnare.png><br>Your account is created.<br> Go to <a href=http://{host}/activate?uuid={uuid}>http://{host}/activate?uuid={uuid}</a>, to activate your account.' where template='activate';
insert into templates (uuid, template, content) values (uuid_generate_v4(), 'reset', '<img src=http://snapsnare.org/static/snapsnare.png><br><br> Seems like you forgot your snapsnare password. To reset it, just click <a href=http://{host}/reset?uuid={uuid}>reset</a> to change your password.');