insert into activities (uuid, content, usr_id, stn_id) values ('4c94d347-cee7-4954-bdac-b28a796db1ff',E'<h4>Contact</h4>\r\n <b>Stichting Computersnaar</b>\r\n Slinge 10\r\n 3084 EB Rotterdam\r\n <a href="mailto: stics010@gmail.com">stics010@gmail.com</a>\r\n telefoon: <a href="tel:+31625246964">+31 (0)6-25246964</a>\r\n KvK: 60471603\r\n\r\n\r\n <h4>Doelstelling</h4>\r\n <b>Stichting Computersnaar</b> stelt zich op het vlak van cultuur en nieuwe media ten doel om mensen op deze terreinen  in staat te stellen hun talenten te ontdekken en ontplooien.\r\n Speciale aandacht gaat hierbij uit naar de jeugd en nieuwe media.\r\n\r\n De nadruk van de stichting ligt op culturele diversiteit, via muziek en nieuwe media.\r\n Onze Missie is om culturele expressie en interactie op gang te brengen en te ondersteunen.\r\n We willen daarbij graag een stem geven aan hen die vaak niet gehoord worden. We koppelen inspirerende mensen en ideeën\r\n met cultureel beleid, op lokaal gebied en door gebruik van nieuwe media ook wereldwijd.\r\n\r\n <h4>Wat bieden wij</h4>\r\n <ul>\r\n <li>Het ondersteunen en begeleiden van muziek opnames, videoclips,  cd\'s, dvd\'s, flyers/posters en cd covers. </li>\r\n <li>Het inzetten van een website waarop bovenstaande en andere culturele activiteiten kunnen worden bekeken.</li>\r\n <li>Het geven van oriëntatie clinics muziek en video.</li>\r\n <li>Het bieden van een oefenruimte, voor het maken van opnames (muziek studio).</li>\r\n <li> Het beschikbaar stellen van een blue/green screen room.</li>\r\n <li> Het faciliteren van het realiseren van complete muziek producten, videoclips en artwork</li>\r\n </ul>\r\n', get_user_id('admin@computersnaar.nl'), get_section_id('Over ons'));
