insert into activities (uuid, content, usr_id, stn_id) values ('4a643c89-095c-4935-bc67-67458584316e',E'<b>Jamulus</b>\r\nMet Jamulus kun je via het internet samenspelen.\r\nOm dit vlotjes te laten verlopen is het handig als je je instrument kan koppelen met je computer, via USB.\r\nAangezien ik linux gebruik is dit best een dingetje. Er zijn maar weinig fabrikanten, helaas, die hier support voor bieden. De focus lijkt te liggen op Mac en Windows.\r\n\r\nOp <a href="https://amp.reddit.com/r/linuxaudio/comments/apgqmj/officially_linux_supported_audio_interfaces/">Reddit</a> heb ik een posting gevonden die hierover gaat. Hier worden fabrikanten genoemd die support bieden voor Linux. Erg handig.\r\n\r\nEen andere interessante site is <a href="https://wiki.linuxaudio.org/wiki/hardware_support">wiki.linuxaudio.org</a>. Op deze site wordt de linux support status van verschillende usb audio hardware getoond.\r\n\r\n<b>Refenties</b>\r\n<a href="https://amp.reddit.com/r/linuxaudio/comments/apgqmj/officially_linux_supported_audio_interfaces/">linux supported audio interfaces</a>\r\n<a href="https://focusrite.com/en/usb-audio-interface/scarlett/scarlett-2i2">focusrite scarlet 2i2</a>\r\n<a href="https://focusrite.com/en/usb-audio-interface/scarlett/scarlett-4i4">focusrite scarlet 4i4 (+midi)</a>', get_user_id('admin@computersnaar.nl'), get_section_id('Home'));
insert into activities (uuid, content, usr_id, stn_id) values ('621010b5-d7c0-49af-bd10-2c7f28e62afb',E'<b>Jamulus server</b>\r\nJamulus, opensource, wordt gebruikt om over het internet te jammen.\r\nOnze server is beschikbaar via ip adres <b>77.161.84.130</b>\r\n\r\nVoordat je deze server kunt gebruiken heb je een Jamulus Client nodig op je eigen computer.\r\nHieronder vind je de installatie instructies:\r\n<a href="https://jamulus.io/wiki/Installation-for-Windows" target="_blank">Jamulus Client installatie voor Windows:</a>\r\n<a href="https://jamulus.io/wiki/Installation-for-Macintosh" target="_blank">Jamulus Client installatie voor Mac</a>\r\n<a href="https://jamulus.io/wiki/Installation-for-Linux" target="_blank">Jamulus Client installatie voor Linux</a>\r\n\r\nWanneer je Jamulus hebt geinstalleerd kun je na het opstarten van de Jamulus Client op de button <b>connect</b> drukken.\r\n Plak vervolgens <b>77.161.84.130</b> in het tekstvak <b>server adres</b> om te verbinden met onze Jamulus server', get_user_id('admin@computersnaar.nl'), get_section_id('Home'));
